contracts/BulkGift.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title BulkGift
 * @notice Batch processing contract for bulk gifts (team gifts, hackathon winners, etc.)
 * @dev Demonstrates advanced programmable logic for batch operations
 */
contract BulkGift {
    // ============ Structs ============
    
    struct BulkGiftCampaign {
        address sender;
        bytes32 bulkGiftCode;
        uint256 totalAmount;
        uint256 totalRecipients;
        uint256 claimedCount;
        uint256 threshold; // Percentage (e.g., 80 = 80%)
        uint256 deadline;
        bool active;
        bool thresholdReached;
    }
    
    struct Recipient {
        address recipient;
        uint256 amount;
        bytes32 claimCode;
        bool claimed;
    }
    
    // ============ State Variables ============
    
    mapping(bytes32 => BulkGiftCampaign) public campaigns;
    mapping(bytes32 => Recipient[]) public recipients; // bulkGiftCode => Recipients
    mapping(bytes32 => mapping(bytes32 => bool)) public claimCodeExists; // bulkGiftCode => claimCode => exists
    
    address public owner;
    uint256 public totalCampaigns;
    
    // ============ Events ============
    
    event BulkGiftCreated(
        bytes32 indexed bulkGiftCode,
        address indexed sender,
        uint256 totalAmount,
        uint256 recipientCount,
        uint256 threshold
    );
    
    event BulkGiftClaimed(
        bytes32 indexed bulkGiftCode,
        bytes32 indexed claimCode,
        address indexed recipient,
        uint256 amount
    );
    
    event ThresholdReached(
        bytes32 indexed bulkGiftCode,
        uint256 claimedCount,
        uint256 totalRecipients
    );
    
    event BulkGiftRefunded(
        bytes32 indexed bulkGiftCode,
        address indexed sender,
        uint256 refundedAmount
    );
    
    // ============ Modifiers ============
    
    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }
    
    modifier campaignExists(bytes32 bulkGiftCode) {
        require(campaigns[bulkGiftCode].sender != address(0), "Campaign does not exist");
        _;
    }
    
    // ============ Constructor ============
    
    constructor() {
        owner = msg.sender;
    }
    
    // ============ Core Functions ============
    
    /**
     * @notice Create a bulk gift campaign with batch escrow
     * @param bulkGiftCode Unique code for the bulk gift campaign
     * @param recipientAddresses Array of recipient addresses
     * @param amounts Array of amounts (must match recipients length)
     * @param claimCodes Array of unique claim codes for each recipient
     * @param threshold Percentage threshold for auto-release (0-100, 0 = no threshold)
     * @param deadline Timestamp when campaign expires (0 = never expires)
     */
    function createBulkGift(
        bytes32 bulkGiftCode,
        address[] memory recipientAddresses,
        uint256[] memory amounts,
        bytes32[] memory claimCodes,
        uint256 threshold,
        uint256 deadline
    ) external payable {
        require(recipientAddresses.length > 0, "No recipients");
        require(
            recipientAddresses.length == amounts.length,
            "Recipients and amounts length mismatch"
        );
        require(
            recipientAddresses.length == claimCodes.length,
            "Recipients and claim codes length mismatch"
        );
        require(campaigns[bulkGiftCode].sender == address(0), "Campaign already exists");
        require(threshold <= 100, "Threshold must be <= 100");
        
        // Calculate total amount
        uint256 totalAmount = 0;
        for (uint256 i = 0; i < amounts.length; i++) {
            totalAmount += amounts[i];
        }
        
        require(msg.value >= totalAmount, "Insufficient funds");
        
        // Create campaign
        campaigns[bulkGiftCode] = BulkGiftCampaign({
            sender: msg.sender,
            bulkGiftCode: bulkGiftCode,
            totalAmount: totalAmount,
            totalRecipients: recipientAddresses.length,
            claimedCount: 0,
            threshold: threshold,
            deadline: deadline,
            active: true,
            thresholdReached: false
        });
        
        // Create recipients
        for (uint256 i = 0; i < recipientAddresses.length; i++) {
            require(recipientAddresses[i] != address(0), "Invalid recipient");
            require(amounts[i] > 0, "Amount must be greater than 0");
            require(!claimCodeExists[bulkGiftCode][claimCodes[i]], "Duplicate claim code");
            
            recipients[bulkGiftCode].push(Recipient({
                recipient: recipientAddresses[i],
                amount: amounts[i],
                claimCode: claimCodes[i],
                claimed: false
            }));
            
            claimCodeExists[bulkGiftCode][claimCodes[i]] = true;
        }
        
        totalCampaigns++;
        
        emit BulkGiftCreated(
            bulkGiftCode,
            msg.sender,
            totalAmount,
            recipientAddresses.length,
            threshold
        );
    }
    
    /**
     * @notice Claim individual gift from bulk campaign
     * @param bulkGiftCode Bulk gift campaign code
     * @param claimCode Individual claim code
     */
    function claimBulkGift(bytes32 bulkGiftCode, bytes32 claimCode) 
        external 
        campaignExists(bulkGiftCode) 
    {
        BulkGiftCampaign storage campaign = campaigns[bulkGiftCode];
        
        require(campaign.active, "Campaign not active");
        require(
            campaign.deadline == 0 || block.timestamp <= campaign.deadline,
            "Campaign expired"
        );
        
        // Find recipient
        Recipient[] storage recipientList = recipients[bulkGiftCode];
        uint256 recipientIndex = type(uint256).max;
        
        for (uint256 i = 0; i < recipientList.length; i++) {
            if (recipientList[i].claimCode == claimCode) {
                recipientIndex = i;
                break;
            }
        }
        
        require(recipientIndex != type(uint256).max, "Claim code not found");
        require(!recipientList[recipientIndex].claimed, "Already claimed");
        require(
            msg.sender == recipientList[recipientIndex].recipient,
            "Not the recipient"
        );
        
        // Mark as claimed
        recipientList[recipientIndex].claimed = true;
        campaign.claimedCount++;
        
        // Transfer funds
        uint256 amount = recipientList[recipientIndex].amount;
        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "Transfer failed");
        
        emit BulkGiftClaimed(
            bulkGiftCode,
            claimCode,
            msg.sender,
            amount
        );
        
        // Check threshold
        if (campaign.threshold > 0 && !campaign.thresholdReached) {
            uint256 claimedPercentage = (campaign.claimedCount * 100) / campaign.totalRecipients;
            
            if (claimedPercentage >= campaign.threshold) {
                campaign.thresholdReached = true;
                _releaseRemaining(bulkGiftCode);
                
                emit ThresholdReached(
                    bulkGiftCode,
                    campaign.claimedCount,
                    campaign.totalRecipients
                );
            }
        }
    }
    
    /**
     * @notice Release remaining unclaimed gifts when threshold reached
     */
    function _releaseRemaining(bytes32 bulkGiftCode) internal {
        BulkGiftCampaign storage campaign = campaigns[bulkGiftCode];
        Recipient[] storage recipientList = recipients[bulkGiftCode];
        
        // Release all remaining unclaimed gifts
        for (uint256 i = 0; i < recipientList.length; i++) {
            if (!recipientList[i].claimed) {
                recipientList[i].claimed = true;
                campaign.claimedCount++;
                
                (bool success, ) = payable(recipientList[i].recipient).call{
                    value: recipientList[i].amount
                }("");
                require(success, "Release transfer failed");
            }
        }
    }
    
    /**
     * @notice Refund unclaimed gifts after deadline
     * @param bulkGiftCode Bulk gift campaign code
     */
    function refundBulkGift(bytes32 bulkGiftCode) 
        external 
        campaignExists(bulkGiftCode) 
    {
        BulkGiftCampaign storage campaign = campaigns[bulkGiftCode];
        
        require(campaign.active, "Campaign not active");
        require(
            campaign.deadline > 0 && block.timestamp > campaign.deadline,
            "Campaign not expired"
        );
        
        campaign.active = false;
        
        // Calculate unclaimed amount
        uint256 unclaimedAmount = 0;
        Recipient[] storage recipientList = recipients[bulkGiftCode];
        
        for (uint256 i = 0; i < recipientList.length; i++) {
            if (!recipientList[i].claimed) {
                unclaimedAmount += recipientList[i].amount;
            }
        }
        
        // Refund to sender
        if (unclaimedAmount > 0) {
            (bool success, ) = payable(campaign.sender).call{value: unclaimedAmount}("");
            require(success, "Refund transfer failed");
        }
        
        emit BulkGiftRefunded(bulkGiftCode, campaign.sender, unclaimedAmount);
    }
    
    /**
     * @notice Get campaign details
     */
    function getCampaign(bytes32 bulkGiftCode) 
        external 
        view 
        returns (BulkGiftCampaign memory) 
    {
        return campaigns[bulkGiftCode];
    }
    
    /**
     * @notice Get recipient count for campaign
     */
    function getRecipientCount(bytes32 bulkGiftCode) external view returns (uint256) {
        return recipients[bulkGiftCode].length;
    }
    
    /**
     * @notice Get recipient by index
     */
    function getRecipient(bytes32 bulkGiftCode, uint256 index) 
        external 
        view 
        returns (Recipient memory) 
    {
        return recipients[bulkGiftCode][index];
    }
    
    /**
     * @notice Get claim status
     */
    function getClaimStatus(bytes32 bulkGiftCode) 
        external 
        view 
        returns (
            uint256 claimedCount,
            uint256 totalRecipients,
            uint256 claimedPercentage,
            bool thresholdReached
        ) 
    {
        BulkGiftCampaign memory campaign = campaigns[bulkGiftCode];
        claimedCount = campaign.claimedCount;
        totalRecipients = campaign.totalRecipients;
        claimedPercentage = (claimedCount * 100) / totalRecipients;
        thresholdReached = campaign.thresholdReached;
    }
}

contracts/ConditionalRelease.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title ConditionalRelease
 * @notice Advanced conditional release logic (birthday triggers, scheduled, milestone-based)
 * @dev Demonstrates programmable logic for conditional releases
 */
contract ConditionalRelease {
    // ============ Structs ============
    
    struct ConditionalGift {
        address sender;
        address recipient;
        uint256 amount;
        ReleaseCondition condition;
        uint256 triggerValue; // Timestamp or milestone ID
        bool released;
        string message;
    }
    
    enum ReleaseCondition {
        NONE,           // No condition
        BIRTHDAY,       // Release on specific date
        SCHEDULED,      // Release at specific timestamp
        MILESTONE,      // Release when milestone reached
        EVENT_TRIGGERED // Release when event occurs
    }
    
    // ============ State Variables ============
    
    mapping(bytes32 => ConditionalGift) public conditionalGifts;
    mapping(bytes32 => bool) public milestones; // milestoneId => reached
    mapping(bytes32 => bool) public events; // eventId => occurred
    
    address public owner;
    uint256 public totalConditionalGifts;
    uint256 public totalReleased;
    
    // ============ Events ============
    
    event ConditionalGiftCreated(
        bytes32 indexed giftId,
        address indexed sender,
        address indexed recipient,
        uint256 amount,
        ReleaseCondition condition,
        uint256 triggerValue
    );
    
    event GiftReleased(
        bytes32 indexed giftId,
        address indexed recipient,
        uint256 amount,
        ReleaseCondition condition
    );
    
    event MilestoneReached(bytes32 indexed milestoneId);
    
    event EventTriggered(bytes32 indexed eventId);
    
    // ============ Modifiers ============
    
    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }
    
    modifier giftExists(bytes32 giftId) {
        require(conditionalGifts[giftId].sender != address(0), "Gift does not exist");
        _;
    }
    
    // ============ Constructor ============
    
    constructor() {
        owner = msg.sender;
    }
    
    // ============ Core Functions ============
    
    /**
     * @notice Create a conditional gift
     * @param giftId Unique identifier for the gift
     * @param recipient Address of the recipient
     * @param condition Type of release condition
     * @param triggerValue Value for condition (timestamp for time-based, milestone ID for milestone-based)
     * @param message Optional gift message
     */
    function createConditionalGift(
        bytes32 giftId,
        address recipient,
        ReleaseCondition condition,
        uint256 triggerValue,
        string memory message
    ) external payable {
        require(recipient != address(0), "Invalid recipient");
        require(msg.value > 0, "Amount must be greater than 0");
        require(
            conditionalGifts[giftId].sender == address(0),
            "Gift already exists"
        );
        
        // Validate trigger value based on condition
        if (condition == ReleaseCondition.SCHEDULED || 
            condition == ReleaseCondition.BIRTHDAY) {
            require(triggerValue > block.timestamp, "Trigger must be in future");
        }
        
        conditionalGifts[giftId] = ConditionalGift({
            sender: msg.sender,
            recipient: recipient,
            amount: msg.value,
            condition: condition,
            triggerValue: triggerValue,
            released: false,
            message: message
        });
        
        totalConditionalGifts++;
        
        emit ConditionalGiftCreated(
            giftId,
            msg.sender,
            recipient,
            msg.value,
            condition,
            triggerValue
        );
    }
    
    /**
     * @notice Release gift when condition is satisfied
     * @param giftId Unique identifier for the gift
     */
    function releaseGift(bytes32 giftId) external giftExists(giftId) {
        ConditionalGift storage gift = conditionalGifts[giftId];
        
        require(!gift.released, "Gift already released");
        require(_checkCondition(gift), "Condition not satisfied");
        
        gift.released = true;
        totalReleased++;
        
        // Transfer funds
        (bool success, ) = payable(gift.recipient).call{value: gift.amount}("");
        require(success, "Transfer failed");
        
        emit GiftReleased(
            giftId,
            gift.recipient,
            gift.amount,
            gift.condition
        );
    }
    
    /**
     * @notice Check if condition is satisfied
     */
    function _checkCondition(ConditionalGift memory gift) internal view returns (bool) {
        if (gift.condition == ReleaseCondition.NONE) {
            return true;
        }
        
        if (gift.condition == ReleaseCondition.SCHEDULED) {
            return block.timestamp >= gift.triggerValue;
        }
        
        if (gift.condition == ReleaseCondition.BIRTHDAY) {
            // Check if current date matches trigger date (simplified - checks timestamp)
            return block.timestamp >= gift.triggerValue;
        }
        
        if (gift.condition == ReleaseCondition.MILESTONE) {
            bytes32 milestoneId = bytes32(gift.triggerValue);
            return milestones[milestoneId];
        }
        
        if (gift.condition == ReleaseCondition.EVENT_TRIGGERED) {
            bytes32 eventId = bytes32(gift.triggerValue);
            return events[eventId];
        }
        
        return false;
    }
    
    /**
     * @notice Mark milestone as reached (for milestone-based releases)
     * @param milestoneId Unique milestone identifier
     */
    function reachMilestone(bytes32 milestoneId) external onlyOwner {
        require(!milestones[milestoneId], "Milestone already reached");
        
        milestones[milestoneId] = true;
        
        emit MilestoneReached(milestoneId);
    }
    
    /**
     * @notice Trigger event (for event-triggered releases)
     * @param eventId Unique event identifier
     */
    function triggerEvent(bytes32 eventId) external onlyOwner {
        require(!events[eventId], "Event already triggered");
        
        events[eventId] = true;
        
        emit EventTriggered(eventId);
    }
    
    /**
     * @notice Batch release multiple gifts (for scheduled releases)
     * @param giftIds Array of gift IDs to release
     */
    function batchRelease(bytes32[] memory giftIds) external {
        for (uint256 i = 0; i < giftIds.length; i++) {
            ConditionalGift storage gift = conditionalGifts[giftIds[i]];
            
            if (!gift.released && _checkCondition(gift)) {
                gift.released = true;
                totalReleased++;
                
                (bool success, ) = payable(gift.recipient).call{value: gift.amount}("");
                require(success, "Batch release transfer failed");
                
                emit GiftReleased(
                    giftIds[i],
                    gift.recipient,
                    gift.amount,
                    gift.condition
                );
            }
        }
    }
    
    /**
     * @notice Get gift details
     */
    function getGift(bytes32 giftId) external view returns (ConditionalGift memory) {
        return conditionalGifts[giftId];
    }
    
    /**
     * @notice Check if gift can be released
     */
    function canRelease(bytes32 giftId) external view returns (bool) {
        ConditionalGift memory gift = conditionalGifts[giftId];
        
        if (gift.sender == address(0)) return false;
        if (gift.released) return false;
        
        return _checkCondition(gift);
    }
}

contracts/GiftEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title GiftEscrow
 * @notice Advanced escrow contract with programmable logic beyond basic transfers
 * @dev Supports conditional releases, time locks, secret codes, and automatic refunds
 */
contract GiftEscrow {
    // ============ Structs ============
    
    struct Gift {
        address sender;
        address recipient;
        uint256 amount;
        uint256 unlockTime; // Timestamp when gift can be claimed
        uint256 expirationTime; // Timestamp when gift expires
        bytes32 secretHash; // Hash of secret code (if required)
        bool claimed;
        bool refunded;
        string message; // Optional gift message
    }
    
    struct Condition {
        ConditionType conditionType;
        uint256 triggerValue; // Timestamp for time-based, milestone ID for milestone-based
        bool satisfied;
    }
    
    enum ConditionType {
        NONE,           // No condition, can claim immediately
        TIME_LOCKED,    // Cannot claim before unlockTime
        BIRTHDAY,       // Triggered on specific date
        SCHEDULED,      // Scheduled release at specific time
        SECRET_REQUIRED // Requires secret code
    }
    
    // ============ State Variables ============
    
    mapping(bytes32 => Gift) public gifts; // claimCode => Gift
    mapping(bytes32 => Condition) public conditions; // claimCode => Condition
    mapping(address => uint256) public balances; // Sender balances for refunds
    
    address public owner;
    uint256 public totalGifts;
    uint256 public totalClaimed;
    uint256 public totalRefunded;
    
    // ============ Events ============
    
    event GiftCreated(
        bytes32 indexed claimCode,
        address indexed sender,
        address indexed recipient,
        uint256 amount,
        uint256 unlockTime,
        uint256 expirationTime
    );
    
    event GiftClaimed(
        bytes32 indexed claimCode,
        address indexed recipient,
        uint256 amount
    );
    
    event GiftRefunded(
        bytes32 indexed claimCode,
        address indexed sender,
        uint256 amount
    );
    
    event ConditionSatisfied(
        bytes32 indexed claimCode,
        ConditionType conditionType
    );
    
    // ============ Modifiers ============
    
    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }
    
    modifier giftExists(bytes32 claimCode) {
        require(gifts[claimCode].sender != address(0), "Gift does not exist");
        _;
    }
    
    // ============ Constructor ============
    
    constructor() {
        owner = msg.sender;
    }
    
    // ============ Core Functions ============
    
    /**
     * @notice Create a gift escrow with programmable conditions
     * @param recipient Address of the gift recipient
     * @param claimCode Unique claim code for the gift
     * @param unlockTime Timestamp when gift can be claimed (0 = immediate)
     * @param expirationTime Timestamp when gift expires (0 = never expires)
     * @param secretHash Hash of secret code (bytes32(0) = no secret required)
     * @param conditionType Type of condition for release
     * @param triggerValue Value for condition (timestamp for time-based, etc.)
     * @param message Optional gift message
     */
    function createGift(
        address recipient,
        bytes32 claimCode,
        uint256 unlockTime,
        uint256 expirationTime,
        bytes32 secretHash,
        ConditionType conditionType,
        uint256 triggerValue,
        string memory message
    ) external payable {
        require(recipient != address(0), "Invalid recipient");
        require(msg.value > 0, "Amount must be greater than 0");
        require(gifts[claimCode].sender == address(0), "Gift already exists");
        require(
            expirationTime == 0 || expirationTime > block.timestamp,
            "Expiration must be in future"
        );
        
        // Create gift
        gifts[claimCode] = Gift({
            sender: msg.sender,
            recipient: recipient,
            amount: msg.value,
            unlockTime: unlockTime,
            expirationTime: expirationTime,
            secretHash: secretHash,
            claimed: false,
            refunded: false,
            message: message
        });
        
        // Create condition
        conditions[claimCode] = Condition({
            conditionType: conditionType,
            triggerValue: triggerValue,
            satisfied: conditionType == ConditionType.NONE
        });
        
        // Update balances for refund tracking
        balances[msg.sender] += msg.value;
        
        totalGifts++;
        
        emit GiftCreated(
            claimCode,
            msg.sender,
            recipient,
            msg.value,
            unlockTime,
            expirationTime
        );
    }
    
    /**
     * @notice Claim a gift (with optional secret code)
     * @param claimCode Unique claim code for the gift
     * @param secret Secret code (empty string if not required)
     */
    function claimGift(bytes32 claimCode, string memory secret) external giftExists(claimCode) {
        Gift storage gift = gifts[claimCode];
        Condition storage condition = conditions[claimCode];
        
        // Check if already claimed
        require(!gift.claimed, "Gift already claimed");
        require(!gift.refunded, "Gift already refunded");
        
        // Check if expired
        if (gift.expirationTime > 0 && block.timestamp > gift.expirationTime) {
            _refundGift(claimCode);
            revert("Gift expired");
        }
        
        // Verify recipient
        require(msg.sender == gift.recipient, "Not the recipient");
        
        // Check unlock time
        require(
            gift.unlockTime == 0 || block.timestamp >= gift.unlockTime,
            "Gift not yet unlocked"
        );
        
        // Check condition
        require(_checkCondition(claimCode), "Condition not satisfied");
        
        // Verify secret if required
        if (condition.conditionType == ConditionType.SECRET_REQUIRED) {
            require(
                gift.secretHash == keccak256(abi.encodePacked(secret)),
                "Invalid secret code"
            );
        }
        
        // Mark as claimed
        gift.claimed = true;
        balances[gift.sender] -= gift.amount;
        totalClaimed++;
        
        // Transfer funds
        (bool success, ) = payable(gift.recipient).call{value: gift.amount}("");
        require(success, "Transfer failed");
        
        emit GiftClaimed(claimCode, gift.recipient, gift.amount);
    }
    
    /**
     * @notice Refund an expired or unclaimed gift
     * @param claimCode Unique claim code for the gift
     */
    function refundGift(bytes32 claimCode) external giftExists(claimCode) {
        Gift storage gift = gifts[claimCode];
        
        require(!gift.claimed, "Gift already claimed");
        require(!gift.refunded, "Gift already refunded");
        require(
            gift.expirationTime > 0 && block.timestamp > gift.expirationTime,
            "Gift not expired"
        );
        
        _refundGift(claimCode);
    }
    
    /**
     * @notice Internal function to refund a gift
     */
    function _refundGift(bytes32 claimCode) internal {
        Gift storage gift = gifts[claimCode];
        
        gift.refunded = true;
        balances[gift.sender] -= gift.amount;
        totalRefunded++;
        
        (bool success, ) = payable(gift.sender).call{value: gift.amount}("");
        require(success, "Refund transfer failed");
        
        emit GiftRefunded(claimCode, gift.sender, gift.amount);
    }
    
    /**
     * @notice Check if condition is satisfied
     */
    function _checkCondition(bytes32 claimCode) internal view returns (bool) {
        Condition memory condition = conditions[claimCode];
        
        if (condition.conditionType == ConditionType.NONE) {
            return true;
        }
        
        if (condition.conditionType == ConditionType.TIME_LOCKED) {
            return block.timestamp >= condition.triggerValue;
        }
        
        if (condition.conditionType == ConditionType.BIRTHDAY) {
            // Check if current date matches trigger date (simplified)
            return block.timestamp >= condition.triggerValue;
        }
        
        if (condition.conditionType == ConditionType.SCHEDULED) {
            return block.timestamp >= condition.triggerValue;
        }
        
        // SECRET_REQUIRED is checked in claimGift function
        return true;
    }
    
    /**
     * @notice Satisfy a condition (for external triggers like birthday)
     * @param claimCode Unique claim code
     */
    function satisfyCondition(bytes32 claimCode) external giftExists(claimCode) {
        Condition storage condition = conditions[claimCode];
        
        require(!condition.satisfied, "Condition already satisfied");
        
        // Only owner or automated system can satisfy conditions
        require(
            msg.sender == owner || msg.sender == address(this),
            "Not authorized"
        );
        
        condition.satisfied = true;
        
        emit ConditionSatisfied(claimCode, condition.conditionType);
    }
    
    /**
     * @notice Get gift details
     */
    function getGift(bytes32 claimCode) external view returns (Gift memory) {
        return gifts[claimCode];
    }
    
    /**
     * @notice Get condition details
     */
    function getCondition(bytes32 claimCode) external view returns (Condition memory) {
        return conditions[claimCode];
    }
    
    /**
     * @notice Check if gift can be claimed
     */
    function canClaim(bytes32 claimCode) external view returns (bool) {
        Gift memory gift = gifts[claimCode];
        
        if (gift.sender == address(0)) return false;
        if (gift.claimed) return false;
        if (gift.refunded) return false;
        if (gift.expirationTime > 0 && block.timestamp > gift.expirationTime) return false;
        if (gift.unlockTime > 0 && block.timestamp < gift.unlockTime) return false;
        
        return _checkCondition(claimCode);
    }
    
    // ============ Admin Functions ============
    
    /**
     * @notice Withdraw contract balance (emergency only)
     */
    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No balance to withdraw");
        
        (bool success, ) = payable(owner).call{value: balance}("");
        require(success, "Withdraw failed");
    }
}

contracts/GroupGiftEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title GroupGiftEscrow
 * @notice Multi-contributor gift coordination with threshold-based release
 * @dev Demonstrates programmable logic for group coordination
 */
contract GroupGiftEscrow {
    // ============ Structs ============
    
    struct GroupGift {
        address initiator;
        address recipient;
        uint256 targetAmount;
        uint256 currentAmount;
        uint256 deadline;
        uint256 contributorCount;
        bool active;
        bool distributed;
    }
    
    struct Contribution {
        address contributor;
        uint256 amount;
        uint256 timestamp;
        bool refunded;
    }
    
    // ============ State Variables ============
    
    mapping(bytes32 => GroupGift) public groupGifts;
    mapping(bytes32 => Contribution[]) public contributions; // groupGiftId => Contributions
    mapping(bytes32 => mapping(address => bool)) public hasContributed; // groupGiftId => contributor => contributed
    
    address public owner;
    uint256 public totalGroupGifts;
    
    // ============ Events ============
    
    event GroupGiftCreated(
        bytes32 indexed groupGiftId,
        address indexed initiator,
        address indexed recipient,
        uint256 targetAmount,
        uint256 deadline
    );
    
    event ContributionMade(
        bytes32 indexed groupGiftId,
        address indexed contributor,
        uint256 amount,
        uint256 currentAmount,
        uint256 targetAmount
    );
    
    event ThresholdReached(
        bytes32 indexed groupGiftId,
        address indexed recipient,
        uint256 totalAmount
    );
    
    event GroupGiftDistributed(
        bytes32 indexed groupGiftId,
        address indexed recipient,
        uint256 amount
    );
    
    event GroupGiftRefunded(
        bytes32 indexed groupGiftId,
        address indexed contributor,
        uint256 amount
    );
    
    // ============ Modifiers ============
    
    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }
    
    modifier groupGiftExists(bytes32 groupGiftId) {
        require(groupGifts[groupGiftId].initiator != address(0), "Group gift does not exist");
        _;
    }
    
    // ============ Constructor ============
    
    constructor() {
        owner = msg.sender;
    }
    
    // ============ Core Functions ============
    
    /**
     * @notice Create a group gift campaign
     * @param groupGiftId Unique identifier for the group gift
     * @param recipient Address of the gift recipient
     * @param targetAmount Target amount to reach before distribution
     * @param deadline Timestamp when campaign expires
     */
    function createGroupGift(
        bytes32 groupGiftId,
        address recipient,
        uint256 targetAmount,
        uint256 deadline
    ) external {
        require(recipient != address(0), "Invalid recipient");
        require(targetAmount > 0, "Target amount must be greater than 0");
        require(
            deadline == 0 || deadline > block.timestamp,
            "Deadline must be in future"
        );
        require(
            groupGifts[groupGiftId].initiator == address(0),
            "Group gift already exists"
        );
        
        groupGifts[groupGiftId] = GroupGift({
            initiator: msg.sender,
            recipient: recipient,
            targetAmount: targetAmount,
            currentAmount: 0,
            deadline: deadline,
            contributorCount: 0,
            active: true,
            distributed: false
        });
        
        totalGroupGifts++;
        
        emit GroupGiftCreated(
            groupGiftId,
            msg.sender,
            recipient,
            targetAmount,
            deadline
        );
    }
    
    /**
     * @notice Contribute to a group gift
     * @param groupGiftId Unique identifier for the group gift
     */
    function contribute(bytes32 groupGiftId) 
        external 
        payable 
        groupGiftExists(groupGiftId) 
    {
        GroupGift storage gift = groupGifts[groupGiftId];
        
        require(gift.active, "Group gift not active");
        require(!gift.distributed, "Group gift already distributed");
        require(msg.value > 0, "Contribution must be greater than 0");
        require(
            gift.deadline == 0 || block.timestamp <= gift.deadline,
            "Group gift expired"
        );
        
        // Add contribution
        contributions[groupGiftId].push(Contribution({
            contributor: msg.sender,
            amount: msg.value,
            timestamp: block.timestamp,
            refunded: false
        }));
        
        // Track if new contributor
        if (!hasContributed[groupGiftId][msg.sender]) {
            hasContributed[groupGiftId][msg.sender] = true;
            gift.contributorCount++;
        }
        
        // Update current amount
        gift.currentAmount += msg.value;
        
        emit ContributionMade(
            groupGiftId,
            msg.sender,
            msg.value,
            gift.currentAmount,
            gift.targetAmount
        );
        
        // Check if threshold reached
        if (gift.currentAmount >= gift.targetAmount && !gift.distributed) {
            _distributeGroupGift(groupGiftId);
        }
    }
    
    /**
     * @notice Distribute group gift to recipient when threshold reached
     */
    function _distributeGroupGift(bytes32 groupGiftId) internal {
        GroupGift storage gift = groupGifts[groupGiftId];
        
        gift.distributed = true;
        gift.active = false;
        
        // Transfer to recipient
        (bool success, ) = payable(gift.recipient).call{value: gift.currentAmount}("");
        require(success, "Distribution transfer failed");
        
        emit ThresholdReached(groupGiftId, gift.recipient, gift.currentAmount);
        emit GroupGiftDistributed(groupGiftId, gift.recipient, gift.currentAmount);
    }
    
    /**
     * @notice Refund contributions if deadline passed without reaching threshold
     * @param groupGiftId Unique identifier for the group gift
     */
    function refundGroupGift(bytes32 groupGiftId) 
        external 
        groupGiftExists(groupGiftId) 
    {
        GroupGift storage gift = groupGifts[groupGiftId];
        
        require(gift.active, "Group gift not active");
        require(!gift.distributed, "Group gift already distributed");
        require(
            gift.deadline > 0 && block.timestamp > gift.deadline,
            "Deadline not passed"
        );
        require(
            gift.currentAmount < gift.targetAmount,
            "Threshold reached, cannot refund"
        );
        
        gift.active = false;
        
        // Refund all contributions
        Contribution[] storage contribs = contributions[groupGiftId];
        
        for (uint256 i = 0; i < contribs.length; i++) {
            if (!contribs[i].refunded) {
                contribs[i].refunded = true;
                
                (bool success, ) = payable(contribs[i].contributor).call{
                    value: contribs[i].amount
                }("");
                require(success, "Refund transfer failed");
                
                emit GroupGiftRefunded(
                    groupGiftId,
                    contribs[i].contributor,
                    contribs[i].amount
                );
            }
        }
    }
    
    /**
     * @notice Get group gift details
     */
    function getGroupGift(bytes32 groupGiftId) 
        external 
        view 
        returns (GroupGift memory) 
    {
        return groupGifts[groupGiftId];
    }
    
    /**
     * @notice Get contribution count
     */
    function getContributionCount(bytes32 groupGiftId) 
        external 
        view 
        returns (uint256) 
    {
        return contributions[groupGiftId].length;
    }
    
    /**
     * @notice Get contribution by index
     */
    function getContribution(bytes32 groupGiftId, uint256 index) 
        external 
        view 
        returns (Contribution memory) 
    {
        return contributions[groupGiftId][index];
    }
    
    /**
     * @notice Get progress percentage
     */
    function getProgress(bytes32 groupGiftId) 
        external 
        view 
        returns (uint256 percentage) 
    {
        GroupGift memory gift = groupGifts[groupGiftId];
        if (gift.targetAmount == 0) return 0;
        return (gift.currentAmount * 100) / gift.targetAmount;
    }
}

